using System;
using System.Collections.Generic;
using System.Data;
using System.Drawing;
using System.Drawing.Drawing2D;
using System.IO;
using System.Linq;
using System.Runtime.Remoting.Metadata.W3cXsd2001;
using System.Threading.Tasks;
using System.Web;
using System.Web.Http.Results;
using System.Web.Http;
using System.Web.UI;
using System.Web.UI.WebControls;
using Google.Cloud.Firestore;
using Google.Cloud.Language.V1;
using Microsoft.AspNetCore.Mvc;

namespace mallspacium_web.ShopOwner
{
    public partial class AddSalesAndDiscountPage : System.Web.UI.Page
    {
        FirestoreDb database;
        protected void Page_Load(object sender, EventArgs e)
        {
            string path = AppDomain.CurrentDomain.BaseDirectory + @"mallspaceium.json";
            Environment.SetEnvironmentVariable("GOOGLE_APPLICATION_CREDENTIALS", path);

            database = FirestoreDb.Create("mallspaceium");
            shopNameTextbox.Enabled = false;

            getShopName();
        }

        protected void addButton_Click(object sender, EventArgs e)
        {
            AddSaleDiscount();
        }

        public async void AddSaleDiscount()
        {
            // auto-generated unique id
            Random random = new Random();
            int randomIDNumber = random.Next(100000, 999999);
            string saleDiscountID = "SD" + randomIDNumber.ToString();

            // Create an instance of Bitmap from the uploaded file using the FileUpload control
            Bitmap image = new Bitmap(imageFileUpload.PostedFile.InputStream);
            MemoryStream stream = new MemoryStream();
            image.Save(stream, System.Drawing.Imaging.ImageFormat.Jpeg);
            byte[] bytes = stream.ToArray();

            // Convert the Bitmap image to a Base64 string
            string base64String = Convert.ToBase64String(bytes);

            DocumentReference doc = database.Collection("Users").Document((string)Application.Get("usernameget")).Collection("SaleDiscount").Document(saleDiscountID);
            Dictionary<string, object> data1 = new Dictionary<string, object>()
            {
                { "saleDiscId", saleDiscountID },
                { "saleDiscImage", base64String },
                { "saleDiscDesc", descriptionTextbox.Text },
                { "saleDiscStartDate", startDateTextBox.Text },
                { "saleDiscEndDate", endDateTextBox.Text },
                { "saleDiscShopName", shopNameTextbox.Text }
            };

            if (imageFileUploadValidator.IsValid && descriptionTextboxValidator.IsValid && startDateTextboxValidator.IsValid && endDateTextboxValidator.IsValid)
            {
                await doc.SetAsync(data1);


                Boolean hasShopName = true;

                //auto generated unique id
                string notifAaleDiscountID = "SD" + randomIDNumber.ToString();

                DateTime currentDate = DateTime.Now;
                string dateNotifSaleDisc = currentDate.ToString("yyyy-MM-dd HH:mm:ss");


                // Specify the name of the document using a variable or a string literal
                String documentName = shopNameTextbox.Text + " has posted a new sales and discount " + notifAaleDiscountID;

                var usersCollection = database.Collection("Users");
                var shopperQuery = usersCollection.WhereEqualTo("userRole", "Shopper");
                var shopperQuerySnapshot = await shopperQuery.GetSnapshotAsync();

                foreach (var document in shopperQuerySnapshot.Documents)
                {
                    var userId = document.Id;
                    var notificationCollection = usersCollection.Document(userId).Collection("Notification");
                    var documentReference = notificationCollection.Document(documentName);

                    Dictionary<string, object> data = new Dictionary<string, object>();
                    data["notifDetail"] = "Shop " + shopNameTextbox.Text + " posted a new sale & discount available " + notifAaleDiscountID + ".";
                    data["notifImage"] = "iVBORw0KGgoAAAANSUhEUgAAAgAAAAIACAYAAAD0eNT6AAAgAElEQVR4XuydCXgV5fX / v2fm3uwbkISAhIRNCASwSgUCKmirRWVRi / pra / ";
                    data["notifDate"] = dateNotifSaleDisc;
                    data["isSaleDisc"] = hasShopName;
                    data["shopName"] = shopNameTextbox.Text;
                    data["saleDiscDesc"] = descriptionTextbox.Text;
                    data["saleDiscImage"] = base64String;

                    await documentReference.SetAsync(data);
                }

                Query query = database.Collection("Users").WhereEqualTo("userRole", "Shopper");
                QuerySnapshot snapshot = await query.GetSnapshotAsync();

                foreach (DocumentSnapshot documentSnapshot in snapshot.Documents)
                {
                    string token = documentSnapshot.GetValue<string>("token");
                    var deviceToken = token;
                    var title = "New Sale or Discount";
                    var body = "A new sale or discount has been added.";


                    FcmNotification fcm = new FcmNotification();
                    fcm.SendNotification(deviceToken, title, body);

                }


                // Display a message
                ScriptManager.RegisterStartupScript(this, this.GetType(), "alertScript", "alert('Successfully Added a New Sale or Discount');", true);

                // Redirect to another page after a delay
                string url = "MySaleDiscountPage.aspx";
                ScriptManager.RegisterStartupScript(this, this.GetType(), "redirectScript", "setTimeout(function(){ window.location.href = '" + url + "'; }, 1000);", true);
            }
            else
            {
                // Display a message
                        ScriptManager.RegisterStartupScript(this, this.GetType(), "alertScript", "alert('Failed Adding a New Sale or Discount.');", true);
            }
        }


        public async void getShopName()
        {
            if (!IsPostBack)
            {
                Query query = database.Collection("Users").WhereEqualTo("email", (string)Application.Get("usernameget"));
                QuerySnapshot snap = await query.GetSnapshotAsync();

                // Loop through the documents in the query snapshot
                foreach (DocumentSnapshot documentSnapshot in snap.Documents)
                {
                    // Retrieve the data from the document
                    string shopName = documentSnapshot.GetValue<string>("shopName");

                    shopNameTextbox.Text = shopName;
                }
            }
        }
        
    }
}